import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Header.css';

function Header() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Verificar si hay un token en localStorage
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);
  }, []);

  const handleLogout = () => {
    // Eliminar token de localStorage
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    navigate('/');
  };

  return (
    <header className="header animate__animated animate__fadeInDown">
      <div className="logo">
        <Link to="/">Mi Plataforma de Cursos</Link>
      </div>
      <nav className="nav">
        <Link to="/cursos" className="nav-link">Cursos</Link>
        <Link to="/sobre-mi" className="nav-link">Sobre Mí</Link>
        {isLoggedIn ? (
          <button onClick={handleLogout} className="auth-link">Cerrar Sesión</button>
        ) : (
          <>
            <Link to="/login" className="auth-link">Iniciar Sesión</Link>
            <Link to="/registro" className="auth-link">Registrarse</Link>
          </>
        )}
      </nav>
    </header>
  );
}

export default Header;
