import React, { createContext, useState, useEffect, useContext, useCallback } from 'react';
import { jwtDecode } from 'jwt-decode';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [cursosComprados, setCursosComprados] = useState([]);

  const isAuthenticated = useCallback(() => {
    const token = localStorage.getItem('token');
    if (!token) return false;

    try {
      const decoded = jwtDecode(token);
      return decoded.exp * 1000 > Date.now();
    } catch (e) {
      return false;
    }
  }, []);

  const checkAuthStatus = useCallback(async () => {
    const token = localStorage.getItem('token');

    if (token && isAuthenticated()) {
      try {
        const decoded = jwtDecode(token);

        const userResponse = await fetch('http://localhost:5000/api/auth/me', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (userResponse.ok) {
          const userData = await userResponse.json();

          setUser({
            id: decoded.user_id,
            email: userData.data.email,
            nombre: userData.data.nombre,
            role: userData.data.role
          });

          setIsLoggedIn(true);

          // Fetch purchased courses only for students
          if (userData.data.role === 'estudiante') {
            const cursosResponse = await fetch('http://localhost:5000/api/payments/mis-cursos', {
              headers: {
                'Authorization': `Bearer ${token}`
              }
            });

            if (cursosResponse.ok) {
              const cursosData = await cursosResponse.json();
              setCursosComprados(cursosData.data);
            }
          }
        } else {
          // If user fetch fails, logout
          logout();
        }
      } catch (e) {
        console.error("Error verificando estado de autenticación:", e);
        logout();
      }
    } else {
      logout();
    }

    setLoading(false);
  }, [isAuthenticated]);

  const login = useCallback((token) => {
    localStorage.setItem('token', token);
    checkAuthStatus();
  }, [checkAuthStatus]);

  const logout = useCallback(() => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    setUser(null);
    setCursosComprados([]);
  }, []);

  const verificarAccesoCurso = useCallback(async (cursoId) => {
    const token = localStorage.getItem('token');

    if (!token) {
      return { tieneAcceso: false, mensaje: 'No has iniciado sesión' };
    }

    try {
      const response = await fetch(`http://localhost:5000/api/cursos/${cursoId}/verificar-acceso`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.status === 403) {
        return { tieneAcceso: false, mensaje: 'No tienes acceso a este curso' };
      }

      if (!response.ok) {
        return { tieneAcceso: false, mensaje: 'Error al verificar acceso' };
      }

      const data = await response.json();
      return { tieneAcceso: data.tiene_acceso, mensaje: '' };
    } catch (error) {
      return { tieneAcceso: false, mensaje: 'Error de conexión' };
    }
  }, []);

  const procesarPago = useCallback(async (cursoId) => {
    const token = localStorage.getItem('token');

    if (!token) {
      throw new Error('No has iniciado sesión');
    }

    try {
      const response = await fetch('http://localhost:5000/api/payments/create-session', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ curso_id: cursoId })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Error al procesar pago');
      }

      const data = await response.json();
      return data;
    } catch (error) {
      throw error;
    }
  }, []);

  // Check authentication status on mount and when token changes
  useEffect(() => {
    checkAuthStatus();

    // Listen for token changes in other tabs/windows
    const handleStorageChange = (event) => {
      if (event.key === 'token') {
        checkAuthStatus();
      }
    };

    window.addEventListener('storage', handleStorageChange);
    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, [checkAuthStatus]);

  const value = {
    isLoggedIn,
    loading,
    user,
    cursosComprados,
    login,
    logout,
    verificarAccesoCurso,
    procesarPago
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth debe ser usado dentro de un AuthProvider');
  }
  return context;
};
