import React, { createContext, useState, useEffect, useContext, useCallback } from 'react';
import { jwtDecode } from 'jwt-decode';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [cursosComprados, setCursosComprados] = useState([]);

  const isAuthenticated = useCallback(() => {
    const token = localStorage.getItem('token');
    if (!token) return false;

    try {
      const decoded = jwtDecode(token);
      return decoded.exp * 1000 > Date.now();
    } catch (e) {
      return false;
    }
  }, []);

  const checkAuthStatus = useCallback(async () => {
    const authenticated = isAuthenticated();
    const token = localStorage.getItem('token');

    setIsLoggedIn(authenticated);

    if (authenticated && token) {
      try {
        const decoded = jwtDecode(token);
        setUser({
          id: decoded.user_id,
          email: decoded.email,
          rol: decoded.rol
        });

        // Obtener cursos comprados
        const response = await fetch('/api/pagos/mis-cursos', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        if (response.ok) {
          const data = await response.json();
          setCursosComprados(data);
        }
      } catch (e) {
        console.error("Error decoding token:", e);
      }
    } else {
      setUser(null);
      setCursosComprados([]);
    }

    setLoading(false);
  }, [isAuthenticated]);

  const login = useCallback((token) => {
    localStorage.setItem('token', token);
    checkAuthStatus();
  }, [checkAuthStatus]);

  const logout = useCallback(() => {
    localStorage.removeItem('token');
    checkAuthStatus();
  }, [checkAuthStatus]);

  const verificarAccesoCurso = useCallback(async (cursoId) => {
    try {
      const response = await fetch(`/api/cursos/${cursoId}/verificar-acceso`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      return response.ok;
    } catch (error) {
      return false;
    }
  }, []);

  useEffect(() => {
    checkAuthStatus();
  }, [checkAuthStatus]);

  const value = {
    isLoggedIn,
    loading,
    user,
    cursosComprados,
    login,
    logout,
    verificarAccesoCurso
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth debe ser usado dentro de un AuthProvider');
  }
  return context;
};
