import React, { useState, useEffect, useMemo } from 'react';
import { useParams } from 'react-router-dom';
import VideoPlayer from './VideoPlayer';
import './CursoDetalle.css';

function CursoDetalle() {
  const { id } = useParams();
  const [curso, setCurso] = useState(null);
  const [currentVideo, setCurrentVideo] = useState(0);
  const [completedVideos, setCompletedVideos] = useState({});

  // Datos de ejemplo de cursos
  const cursos = useMemo(() => [
    {
      id: 1,
      titulo: 'Curso de React',
      descripcion: 'Aprende React desde cero hasta un nivel avanzado con proyectos pr치cticos.',
      videos: [
        {
          id: 1,
          titulo: 'Introducci칩n a React',
          url: 'https://ejemplo.com/video-react-intro.mp4',
        },
        {
          id: 2,
          titulo: 'Componentes y Props',
          url: 'https://ejemplo.com/video-react-componentes.mp4',
        }
      ]
    }
  ], []);

  useEffect(() => {
    // Buscar el curso por ID
    const cursoEncontrado = cursos.find((c) => c.id === parseInt(id));
    setCurso(cursoEncontrado);
  }, [id, cursos]);

  // Funciones de control de video
  const changeVideo = (index) => {
    if (index >= 0 && index < curso.videos.length) {
      setCurrentVideo(index);
    }
  };

  const markAsCompleted = (videoId) => {
    setCompletedVideos(prev => ({
      ...prev,
      [videoId]: !prev[videoId]
    }));
  };

  if (!curso) {
    return <div className="error">Curso no encontrado</div>;
  }

  return (
    <div className="curso-detalle">
      <h2>{curso.titulo}</h2>
      <p>{curso.descripcion}</p>

      <VideoPlayer 
        videos={curso.videos}
        currentVideo={currentVideo}
        changeVideo={changeVideo}
        completedVideos={completedVideos}
        markAsCompleted={markAsCompleted}
      />
    </div>
  );
}

export default CursoDetalle;
