import React from 'react';
import { Link } from 'react-router-dom';
import './Cursos.css';

function Cursos() {
  const cursos = [
    { id: 1, titulo: 'Curso de React', descripcion: 'Aprende React desde cero.' },
    { id: 2, titulo: 'Curso de Node.js', descripcion: 'Domina el backend con Node.js.' },
    { id: 3, titulo: 'Curso de Dise単o Web', descripcion: 'Crea dise単os modernos y responsivos.' },
  ];

  return (
    <div className="cursos">
      <h2>Nuestros Cursos</h2>
      <ul>
        {cursos.map((curso) => (
          <li key={curso.id}>
            <Link to={`/curso/${curso.id}`}>{curso.titulo}</Link>
            <p>{curso.descripcion}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Cursos;
