import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Header.css';

function Header() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    // Verificar si hay un token en localStorage
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);

    // Cerrar menú al cambiar de tamaño de ventana
    const handleResize = () => {
      if (window.innerWidth > 480 && menuOpen) {
        setMenuOpen(false);
      }
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, [menuOpen]);

  const handleLogout = () => {
    // Eliminar token de localStorage
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    setMenuOpen(false);

    // Navegar a home después de cerrar sesión
    navigate('/', { replace: true });
  };

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  // Función para cerrar el menú al hacer clic en un enlace
  const closeMenu = () => {
    if (menuOpen) setMenuOpen(false);
  };

  // Función para asegurar la navegación
  const navigateTo = (path, event) => {
    event.preventDefault();
    closeMenu();
    navigate(path, { replace: true });
  };

  return (
    <header className="header animate__animated animate__fadeInDown">
      <div className="logo">
        <a href="/" onClick={(e) => navigateTo('/', e)}>Mi Plataforma de Cursos</a>
      </div>

      <button className="menu-toggle" onClick={toggleMenu} aria-label="Abrir menú">
        {menuOpen ? '✕' : '☰'}
      </button>

      <nav className={`nav ${menuOpen ? 'active' : ''}`}>
        <a href="/cursos" className="nav-link" onClick={(e) => navigateTo('/cursos', e)}>Cursos</a>
        <a href="/sobre-mi" className="nav-link" onClick={(e) => navigateTo('/sobre-mi', e)}>Sobre Mí</a>
        {isLoggedIn ? (
          <button onClick={handleLogout} className="auth-link">Cerrar Sesión</button>
        ) : (
          <>
            <a href="/login" className="auth-link" onClick={(e) => navigateTo('/login', e)}>Iniciar Sesión</a>
            <a href="/registro" className="auth-link" onClick={(e) => navigateTo('/registro', e)}>Registrarse</a>
          </>
        )}
      </nav>
    </header>
  );
}

export default Header;
