import React, { useState, useEffect, useRef } from 'react';
import './Home.css';
import 'animate.css'; // Asegúrate de tener animate.css instalado

// Importa tus imágenes
import img1 from './img/imagen1.jpg';
import img2 from './img/imagen2.jpg';

function Home() {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const sliderRef = useRef(null);
  const images = [img1, img2];

  // Configuración del slider
  useEffect(() => {
    // Intervalo de cambio (3 segundos)
    const interval = setInterval(() => {
      setCurrentImageIndex(prev => (prev + 1) % images.length);
    }, 3000);

    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <div className="home">
      {/* Slider de imágenes */}
      <div className="image-slider" ref={sliderRef}>
        <div className="image-overlay"></div>
        {images.map((img, index) => (
          <img
            key={index}
            src={img}
            alt={`Imagen ${index + 1}`}
            className={`slider-image ${
              index === currentImageIndex ? 'active' : 'next'
            }`}
            loading="eager"
          />
        ))}
      </div>

      {/* Contenido principal con animaciones */}
      <div className="hero-content">
        <div className="hero-container">
          <h1 className="hero-title animate__animated animate__fadeInUp animate__faster">
            Plataforma de Cursos Premium
          </h1>

          <p className="hero-subtitle animate__animated animate__fadeInUp animate__faster animate__delay-05s">
            Aprende con los mejores profesionales y lleva tus habilidades al siguiente nivel 
            con nuestros cursos especializados y actualizados.
          </p>

          <div className="hero-cta-container animate__animated animate__fadeInUp animate__faster animate__delay-1s">
            <a href="/cursos" className="hero-cta">Explorar Cursos</a>
            <a href="/registro" className="hero-cta secondary">Prueba Gratis</a>
          </div>

          <div className="features-grid animate__animated animate__fadeInUp animate__faster animate__delay-15s">
            <div className="feature-card">
              <div className="feature-icon">🎓</div>
              <h3 className="feature-title">Certificación</h3>
              <p className="feature-description">Obtén certificados reconocidos en la industria</p>
            </div>

            <div className="feature-card">
              <div className="feature-icon">👨‍💻</div>
              <h3 className="feature-title">Proyectos Reales</h3>
              <p className="feature-description">Aprende trabajando en casos prácticos actuales</p>
            </div>

            <div className="feature-card">
              <div className="feature-icon">📱</div>
              <h3 className="feature-title">Acceso Móvil</h3>
              <p className="feature-description">Aprende desde cualquier dispositivo</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
