import React, { createContext, useState, useEffect, useContext, useCallback } from 'react';
import { jwtDecode } from 'jwt-decode';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [cursosComprados, setCursosComprados] = useState([]);

  const isAuthenticated = useCallback(() => {
    const token = localStorage.getItem('token');
    if (!token) return false;

    try {
      const decoded = jwtDecode(token);
      return decoded.exp * 1000 > Date.now();
    } catch (e) {
      console.error("Error decoding token:", e);
      return false;
    }
  }, []);

  const fetchUserData = useCallback(async (token) => {
    try {
      const decoded = jwtDecode(token);

      const [userResponse, cursosResponse] = await Promise.all([
        fetch('/api/auth/me', {
          headers: { 'Authorization': `Bearer ${token}` }
        }),
        decoded.role === 'estudiante' ? 
          fetch('/api/pagos/mis-cursos', {
            headers: { 'Authorization': `Bearer ${token}` }
          }) : Promise.resolve(null)
      ]);

      if (!userResponse.ok) throw new Error('Failed to fetch user data');

      const userData = await userResponse.json();
      const updatedUser = {
        id: decoded.user_id,
        email: userData.email,
        nombre: userData.nombre,
        role: userData.role
      };
      setUser(updatedUser);

      if (cursosResponse && cursosResponse.ok) {
        const cursosData = await cursosResponse.json();
        setCursosComprados(cursosData);
      } else {
        setCursosComprados([]);
      }

      return updatedUser;
    } catch (error) {
      console.error("Error fetching user data:", error);
      throw error;
    }
  }, []);

  const checkAuthStatus = useCallback(async () => {
    try {
      setLoading(true);
      const authenticated = isAuthenticated();
      setIsLoggedIn(authenticated);

      if (authenticated) {
        const token = localStorage.getItem('token');
        await fetchUserData(token);
      } else {
        setUser(null);
        setCursosComprados([]);
      }
    } catch (error) {
      console.error("Authentication check failed:", error);
      setIsLoggedIn(false);
      setUser(null);
      setCursosComprados([]);
    } finally {
      setLoading(false);
    }
  }, [isAuthenticated, fetchUserData]);

  const login = useCallback(async (token) => {
    try {
      localStorage.setItem('token', token);
      const userData = await fetchUserData(token);
      setIsLoggedIn(true);
      return userData;
    } catch (error) {
      console.error("Login failed:", error);
      logout();
      throw error;
    }
  }, [fetchUserData, logout]);

  const logout = useCallback(() => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    setUser(null);
    setCursosComprados([]);
  }, []);

  const verificarAccesoCurso = useCallback(async (cursoId) => {
    if (!isLoggedIn) return { tieneAcceso: false, mensaje: 'Debes iniciar sesi贸n' };

    try {
      const response = await fetch(`/api/cursos/${cursoId}/verificar-acceso`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (!response.ok) {
        const errorData = await response.json();
        return { 
          tieneAcceso: false, 
          mensaje: errorData.error || 'Error al verificar acceso' 
        };
      }

      const data = await response.json();
      return { tieneAcceso: data.tiene_acceso, mensaje: '' };
    } catch (error) {
      console.error("Error verifying course access:", error);
      return { tieneAcceso: false, mensaje: 'Error de conexi贸n' };
    }
  }, [isLoggedIn]);

  const procesarPago = useCallback(async (cursoId) => {
    try {
      const response = await fetch('/api/payments', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({ curso_id: cursoId })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Error al procesar pago');
      }

      const data = await response.json();

      if (data.token) {
        await login(data.token);
      } else {
        await checkAuthStatus();
      }

      return data;
    } catch (error) {
      console.error("Payment processing error:", error);
      throw error;
    }
  }, [login, checkAuthStatus]);

  useEffect(() => {
    const initializeAuth = async () => {
      await checkAuthStatus();

      // Configurar intervalo para verificar autenticaci贸n peri贸dicamente
      const interval = setInterval(checkAuthStatus, 5 * 60 * 1000); // 5 minutos
      return () => clearInterval(interval);
    };

    initializeAuth();
  }, [checkAuthStatus]);

  const value = {
    isLoggedIn,
    loading,
    user,
    cursosComprados,
    login,
    logout,
    verificarAccesoCurso,
    procesarPago,
    checkAuthStatus
  };

  return (
    <AuthProvider value={value}>
      {!loading && children}
    </AuthProvider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth debe ser usado dentro de un AuthProvider');
  }
  return context;
};
