import React, { createContext, useState, useEffect, useContext, useCallback } from 'react';
import { jwtDecode } from 'jwt-decode';

const AuthContext = createContext(null);

// URLs de configuración para facilitar cambios futuros
const API_BASE_URL = import.meta.env.VITE_API_BASE_URL || 'http://localhost:5000';

export const AuthProvider = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [cursosComprados, setCursosComprados] = useState([]);

  // Función de validación de token
  const isTokenValid = useCallback(() => {
    const token = localStorage.getItem('token');
    if (!token) return false;

    try {
      const decoded = jwtDecode(token);
      return decoded.exp * 1000 > Date.now();
    } catch (e) {
      return false;
    }
  }, []);

  // Función de cierre de sesión
  const logout = useCallback(() => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    setUser(null);
    setCursosComprados([]);
    window.dispatchEvent(new Event('logoutSuccess'));
  }, []);

  // Obtener datos del usuario
  const fetchUserData = useCallback(async (token) => {
    try {
      const userResponse = await fetch(`${API_BASE_URL}/api/auth/me`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!userResponse.ok) {
        throw new Error('No se pudieron obtener los datos del usuario');
      }

      const userData = await userResponse.json();
      return userData.data;
    } catch (error) {
      console.error('Error al obtener datos del usuario:', error);
      return null;
    }
  }, []);

  // Obtener cursos del usuario
  const fetchUserCourses = useCallback(async (token, userRole) => {
    if (userRole !== 'estudiante') return [];

    try {
      const cursosResponse = await fetch(`${API_BASE_URL}/api/payments/mis-cursos`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!cursosResponse.ok) {
        throw new Error('No se pudieron obtener los cursos del usuario');
      }

      const cursosData = await cursosResponse.json();
      return cursosData.data || [];
    } catch (error) {
      console.error('Error al obtener cursos del usuario:', error);
      return [];
    }
  }, []);

  // Restaurar sesión
  const restoreSession = useCallback(async () => {
    const token = localStorage.getItem('token');

    if (token && isTokenValid()) {
      try {
        const userData = await fetchUserData(token);

        if (userData) {
          setUser({
            id: userData.id,
            email: userData.email,
            nombre: userData.nombre,
            role: userData.role
          });

          const userCourses = await fetchUserCourses(token, userData.role);
          setCursosComprados(userCourses);

          setIsLoggedIn(true);
        } else {
          logout();
        }
      } catch (error) {
        console.error('Restauración de sesión fallida:', error);
        logout();
      }
    } else {
      logout();
    }

    setLoading(false);
  }, [isTokenValid, fetchUserData, fetchUserCourses, logout]);

  // Función de inicio de sesión
  const login = useCallback((token) => {
    localStorage.setItem('token', token);
    window.dispatchEvent(new Event('loginSuccess'));
    restoreSession();
  }, [restoreSession]);

  // Verificar acceso al curso
  const verificarAccesoCurso = useCallback(async (cursoId) => {
    const token = localStorage.getItem('token');

    if (!token) {
      return { tieneAcceso: false, mensaje: 'No has iniciado sesión' };
    }

    try {
      const response = await fetch(`${API_BASE_URL}/api/cursos/${cursoId}/verificar-acceso`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.status === 403) {
        return { tieneAcceso: false, mensaje: 'No tienes acceso a este curso' };
      }

      if (!response.ok) {
        return { tieneAcceso: false, mensaje: 'Error al verificar acceso' };
      }

      const data = await response.json();
      return { tieneAcceso: data.tiene_acceso, mensaje: '' };
    } catch (error) {
      return { tieneAcceso: false, mensaje: 'Error de conexión' };
    }
  }, []);

  // Procesar pago
  const procesarPago = useCallback(async (cursoId) => {
    const token = localStorage.getItem('token');

    if (!token) {
      throw new Error('No has iniciado sesión');
    }

    try {
      const response = await fetch(`${API_BASE_URL}/api/payments/create-session`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ curso_id: cursoId })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Error al procesar pago');
      }

      const data = await response.json();
      return data;
    } catch (error) {
      throw error;
    }
  }, []);

  // Restaurar sesión y manejar eventos de almacenamiento
  useEffect(() => {
    // Verificación inicial del estado de autenticación
    restoreSession();

    // Manejar cambios de almacenamiento (sincronización entre pestañas)
    const handleStorageChange = (event) => {
      if (event.key === 'token') {
        restoreSession();
      }
    };

    // Escuchar eventos entre pestañas
    window.addEventListener('storage', handleStorageChange);
    window.addEventListener('loginSuccess', restoreSession);
    window.addEventListener('logoutSuccess', restoreSession);

    return () => {
      window.removeEventListener('storage', handleStorageChange);
      window.removeEventListener('loginSuccess', restoreSession);
      window.removeEventListener('logoutSuccess', restoreSession);
    };
  }, [restoreSession]);

  // Proporcionar valor de contexto
  const value = {
    isLoggedIn,
    loading,
    user,
    cursosComprados,
    login,
    logout,
    verificarAccesoCurso,
    procesarPago
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

// Hook personalizado para usar AuthContext
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth debe ser usado dentro de un AuthProvider');
  }
  return context;
};
