// VideoPlayer.js
import React from 'react';
import './VideoPlayer.css';

function VideoPlayer({ videos, currentVideo, changeVideo, completedVideos, markAsCompleted }) {
  const currentVideoData = videos[currentVideo];

  return (
    <div className="video-player">
      <div className="video-container">
        <video controls className="main-video" key={currentVideoData.url}>
          <source src={currentVideoData.url} type="video/mp4" />
          Tu navegador no soporta el elemento de video.
        </video>

        <div className="video-info">
          <h3>{currentVideoData.titulo}</h3>
          {currentVideoData.duracion && <span className="duracion">{currentVideoData.duracion}</span>}
        </div>

        <div className="video-actions">
          <button 
            onClick={() => markAsCompleted(currentVideoData.id)}
            className={`mark-completed-btn ${completedVideos[currentVideoData.id] ? 'completed' : ''}`}
          >
            {completedVideos[currentVideoData.id] ? '✓ Completado' : 'Marcar como completado'}
          </button>
        </div>
      </div>

      <div className="video-list">
        <h4>Contenido del curso</h4>
        <ul>
          {videos.map((video, index) => (
            <li 
              key={video.id} 
              className={`video-item ${currentVideo === index ? 'active' : ''} ${completedVideos[video.id] ? 'completed' : ''}`}
              onClick={() => changeVideo(index)}
            >
              <span className="video-number">{index + 1}</span>
              <span className="video-title">{video.titulo}</span>
              {video.duracion && <span className="video-duration">{video.duracion}</span>}
              {completedVideos[video.id] && <span className="completed-check">✓</span>}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default VideoPlayer;
