import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import './Header.css';

function Header() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);
  const navigate = useNavigate();
  const location = useLocation(); // Para detectar cambios en la ubicación

  useEffect(() => {
    // Verificar si hay un token en localStorage
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);

    // Cerrar menú al cambiar de tamaño de ventana
    const handleResize = () => {
      if (window.innerWidth > 480 && menuOpen) {
        setMenuOpen(false);
      }
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, [menuOpen]);

  // Cerrar el menú cuando cambia la ubicación
  useEffect(() => {
    setMenuOpen(false);
  }, [location]);

  const handleLogout = () => {
    // Eliminar token de localStorage
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    setMenuOpen(false);
    navigate('/', { replace: true }); // Usar replace para evitar problemas con el historial
  };

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const handleNavClick = (e, path) => {
    // Si estamos en la misma página, evitar recargar y solo cerrar el menú
    if (location.pathname === path) {
      e.preventDefault();
      setMenuOpen(false);
    } else {
      // Para otros casos, cerrar el menú y permitir la navegación normal
      setMenuOpen(false);
    }
  };

  return (
    <header className="header animate__animated animate__fadeInDown">
      <div className="logo">
        <Link 
          to="/" 
          onClick={(e) => handleNavClick(e, "/")}
        >
          Mi Plataforma de Cursos
        </Link>
      </div>

      <button className="menu-toggle" onClick={toggleMenu} aria-label="Abrir menú">
        {menuOpen ? '✕' : '☰'}
      </button>

      <nav className={`nav ${menuOpen ? 'active' : ''}`}>
        <Link 
          to="/cursos" 
          className="nav-link" 
          onClick={(e) => handleNavClick(e, "/cursos")}
        >
          Cursos
        </Link>
        <Link 
          to="/sobre-mi" 
          className="nav-link" 
          onClick={(e) => handleNavClick(e, "/sobre-mi")}
        >
          Sobre Mí
        </Link>
        {isLoggedIn ? (
          <button onClick={handleLogout} className="auth-link">Cerrar Sesión</button>
        ) : (
          <>
            <Link 
              to="/login" 
              className="auth-link" 
              onClick={(e) => handleNavClick(e, "/login")}
            >
              Iniciar Sesión
            </Link>
            <Link 
              to="/registro" 
              className="auth-link" 
              onClick={(e) => handleNavClick(e, "/registro")}
            >
              Registrarse
            </Link>
          </>
        )}
      </nav>
    </header>
  );
}

export default Header;
