import React, { useState, useEffect, useRef } from 'react';
import './Home.css';

// Importa tus im√°genes
import img1 from '../img/render7.jpeg';
import img2 from '../img/render8.jpeg';

function Home() {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [isVisible, setIsVisible] = useState(false);
  const sliderRef = useRef(null);
  const images = [img1, img2];

  // Configuraci√≥n del slider
  useEffect(() => {
    // Hacer visible inmediatamente al cargar
    setIsVisible(true);

    // Intervalo de cambio m√°s r√°pido (3 segundos)
    const interval = setInterval(() => {
      setCurrentImageIndex(prev => (prev + 1) % images.length);
    }, 3000);

    // Guardamos la referencia actual en una variable
    const currentRef = sliderRef.current;

    // Observer para detectar cuando el elemento es visible
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
        }
      },
      { threshold: 0.1 }
    );

    if (currentRef) {
      observer.observe(currentRef);
    }

    return () => {
      clearInterval(interval);
      if (currentRef) {
        observer.unobserve(currentRef);
      }
    };
  }, [images.length]);

  return (
    <div className="home">
      {/* Slider con ref y animaci√≥n inicial */}
      <div 
        className="image-slider"
        ref={sliderRef}
        style={{ opacity: isVisible ? 1 : 0, transition: 'opacity 0.5s ease' }}
      >
        <div className="image-overlay"></div>
        {images.map((img, index) => (
          <img
            key={index}
            src={img}
            alt={`Imagen ${index + 1}`}
            className={`slider-image ${
              index === currentImageIndex ? 'active' : 'next'
            }`}
            loading="eager"
            style={{ transitionDelay: isVisible ? '0s' : '0.3s' }}
          />
        ))}
      </div>

      {/* Contenido principal */}
      <div className="hero-content">
        <div className="hero-container">
          <h1 className="hero-title">Plataforma de Cursos Premium</h1>
          <p className="hero-subtitle">
            Aprende con los mejores profesionales y lleva tus habilidades al siguiente nivel 
            con nuestros cursos especializados y actualizados.
          </p>

          <div className="hero-cta-container">
            <a href="/cursos" className="hero-cta">Explorar Cursos</a>
            <a href="/registro" className="hero-cta secondary">Prueba Gratis</a>
          </div>

          <div className="features-grid">
            <div className="feature-card">
              <div className="feature-icon">üéì</div>
              <h3 className="feature-title">Certificaci√≥n</h3>
              <p className="feature-description">Obt√©n certificados reconocidos en la industria</p>
            </div>

            <div className="feature-card">
              <div className="feature-icon">üë®‚Äçüíª</div>
              <h3 className="feature-title">Proyectos Reales</h3>
              <p className="feature-description">Aprende trabajando en casos pr√°cticos actuales</p>
            </div>

            <div className="feature-card">
              <div className="feature-icon">üì±</div>
              <h3 className="feature-title">Acceso M√≥vil</h3>
              <p className="feature-description">Aprende desde cualquier dispositivo</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
