import React, { createContext, useState, useEffect, useContext, useCallback } from 'react';
import { jwtDecode } from 'jwt-decode';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [cursosComprados, setCursosComprados] = useState([]);

  // Validación robusta del token JWT
  const isValidJWT = (token) => {
    try {
      if (typeof token !== 'string') return false;
      const parts = token.split('.');
      if (parts.length !== 3) return false;

      // Verificar estructura base64
      window.atob(parts[0]);
      window.atob(parts[1]);
      return true;
    } catch {
      return false;
    }
  };

  const isAuthenticated = useCallback(() => {
    const token = localStorage.getItem('token');
    if (!isValidJWT(token)) {
      localStorage.removeItem('token');
      return false;
    }

    try {
      const { exp } = jwtDecode(token);
      const isExpired = Date.now() >= exp * 1000;
      if (isExpired) localStorage.removeItem('token');
      return !isExpired;
    } catch {
      localStorage.removeItem('token');
      return false;
    }
  }, []);

  const logout = useCallback(() => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    setUser(null);
    setCursosComprados([]);
  }, []);

  const fetchUserData = useCallback(async (token) => {
    if (!isValidJWT(token)) throw new Error('Token inválido');

    try {
      const [userRes, cursosRes] = await Promise.all([
        fetch('/api/auth/me', { 
          headers: { 'Authorization': `Bearer ${token}` } 
        }),
        fetch('/api/pagos/mis-cursos', { 
          headers: { 'Authorization': `Bearer ${token}` } 
        }).catch(() => null) // Ignorar error si falla
      ]);

      if (!userRes.ok) throw new Error('Error al cargar datos');

      const userData = await userRes.json();
      const cursosData = cursosRes?.ok ? await cursosRes.json() : [];

      return { userData, cursosData };
    } catch (error) {
      console.error('Error fetching data:', error);
      throw error;
    }
  }, []);

  const login = useCallback(async (token) => {
    if (!isValidJWT(token)) {
      throw new Error('Formato de token inválido');
    }

    try {
      const { userData, cursosData } = await fetchUserData(token);

      localStorage.setItem('token', token);
      setUser({
        id: userData.id,
        email: userData.email,
        nombre: userData.nombre,
        role: userData.role
      });
      setCursosComprados(userData.role === 'estudiante' ? cursosData : []);
      setIsLoggedIn(true);

      return userData;
    } catch (error) {
      logout();
      throw error;
    }
  }, [fetchUserData, logout]);

  const checkAuthStatus = useCallback(async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const authValid = isAuthenticated();

      if (authValid && token) {
        const { userData, cursosData } = await fetchUserData(token);
        setUser(userData);
        setCursosComprados(userData.role === 'estudiante' ? cursosData : []);
        setIsLoggedIn(true);
      } else {
        logout();
      }
    } catch {
      logout();
    } finally {
      setLoading(false);
    }
  }, [isAuthenticated, fetchUserData, logout]);

  const verificarAccesoCurso = useCallback(async (cursoId) => {
    if (!isLoggedIn) return { acceso: false, error: 'No autenticado' };

    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`/api/cursos/${cursoId}/verificar-acceso`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });

      if (res.status === 401) {
        logout();
        return { acceso: false, error: 'Sesión expirada' };
      }

      const data = await res.json();
      return { acceso: data.tiene_acceso, error: '' };
    } catch {
      return { acceso: false, error: 'Error de conexión' };
    }
  }, [isLoggedIn, logout]);

  const procesarPago = useCallback(async (cursoId) => {
    try {
      const token = localStorage.getItem('token');
      const res = await fetch('/api/payments', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ curso_id: cursoId })
      });

      const data = await res.json();

      if (res.status === 401) {
        logout();
        throw new Error('Sesión expirada');
      }

      if (!res.ok) {
        throw new Error(data.error || 'Error en el pago');
      }

      if (data.token) {
        await login(data.token);
      } else {
        await checkAuthStatus();
      }

      return data;
    } catch (error) {
      throw error;
    }
  }, [checkAuthStatus, login, logout]);

  // Efectos
  useEffect(() => {
    checkAuthStatus();

    const interval = setInterval(() => {
      if (isLoggedIn) checkAuthStatus();
    }, 300000); // 5 minutos

    return () => clearInterval(interval);
  }, [checkAuthStatus, isLoggedIn]);

  return (
    <AuthContext.Provider value={{
      isLoggedIn,
      loading,
      user,
      cursosComprados,
      login,
      logout,
      verificarAccesoCurso,
      procesarPago
    }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) throw new Error('useAuth debe usarse dentro de AuthProvider');
  return context;
};
