import React, { useState, useEffect, useRef } from 'react';
import './Home.css';

// Importa tus imágenes
import img1 from './img/imagen1.jpg';
import img2 from './img/imagen2.jpg';

function Home() {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [isVisible, setIsVisible] = useState(false);
  const sliderRef = useRef(null);
  const images = [img1, img2];

  // Configuración del slider
  useEffect(() => {
    // Hacer visible inmediatamente al cargar
    setIsVisible(true);

    // Intervalo de cambio más rápido (3 segundos)
    const interval = setInterval(() => {
      setCurrentImageIndex(prev => (prev + 1) % images.length);
    }, 3000);

    // Observer para detectar cuando el elemento es visible
    const currentSliderRef = sliderRef.current; // Copiamos la referencia actual
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
        }
      },
      { threshold: 0.1 }
    );

    if (currentSliderRef) {
      observer.observe(currentSliderRef);
    }

    return () => {
      clearInterval(interval);
      if (currentSliderRef) {
        observer.unobserve(currentSliderRef); // Usamos la copia en el cleanup
      }
    };
  }, [images.length]); // Añadimos images.length como dependencia

  return (
    <div className="home">
      {/* Slider con ref y animación inicial */}
      <div 
        className="image-slider"
        ref={sliderRef}
        style={{ opacity: isVisible ? 1 : 0, transition: 'opacity 0.5s ease' }}
      >
        <div className="image-overlay"></div>
        {images.map((img, index) => (
          <img
            key={index}
            src={img}
            alt={`Imagen ${index + 1}`}
            className={`slider-image ${
              index === currentImageIndex ? 'active' : 'next'
            }`}
            loading="eager"
            style={{ transitionDelay: isVisible ? '0s' : '0.3s' }}
          />
        ))}
      </div>

      {/* Contenido principal (se mantiene igual) */}
      {/* ... */}
    </div>
  );
}

export default Home;
