import React, { useState, useEffect, useRef } from 'react';
import './Home.css';

// Importa tus imágenes locales
import img1 from '../img/home/render4.jpeg';
import img2 from '../img/home/render5.jpeg';
import img3 from '../img/home/render6.jpeg';
import img4 from '../img/home/render7.jpeg';
import img5 from '../img/home/render8.jpeg';

function Home() {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [scrolled, setScrolled] = useState(false);
  const sliderRef = useRef(null);
  const contentRefs = useRef([]);
  const images = [img1, img2, img3, img4, img5];

  // Configuración del slider de imágenes
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex(prev => (prev + 1) % images.length);
    }, 4000);

    return () => clearInterval(interval);
  }, [images.length]);

  // Configuración del scroll observer
  useEffect(() => {
    const handleScroll = () => {
      if (sliderRef.current) {
        const sliderPosition = sliderRef.current.getBoundingClientRect().top;
        setScrolled(sliderPosition < -50);
      }

      contentRefs.current.forEach(el => {
        if (el) {
          const rect = el.getBoundingClientRect();
          const isVisible = rect.top < window.innerHeight * 0.85;
          if (isVisible) {
            el.classList.add('visible');
          }
        }
      });
    };

    contentRefs.current = Array.from(document.querySelectorAll('[data-scroll]'));
    window.addEventListener('scroll', handleScroll);
    handleScroll();

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="home">
      {/* Hero Section */}
      <div 
        ref={sliderRef}
        className={`image-slider ${scrolled ? 'scroll-down' : ''}`}
      >
        <div className="image-overlay"></div>
        {images.map((img, index) => (
          <img
            key={index}
            src={img}
            alt={`Render arquitectónico ${index + 1}`}
            className={`slider-image ${
              index === currentImageIndex ? 'active' : 'next'
            }`}
            loading="eager"
          />
        ))}

        <div className="slider-content">
          <h1 className="slider-title">RM RENDERS</h1>
          <p className="slider-subtitle">Visualización Arquitectónica de Alto Impacto</p>
        </div>
      </div>

      {/* Main Content */}
      <main className="main-content">
        {/* About Section */}
        <section className="section about-section" data-scroll="fadeInUp">
          <div className="section-container">
            <h2 className="section-title">Transformamos Conceptos en Realidad</h2>
            <p className="section-text">
              Especializados en renders arquitectónicos fotorrealistas, nuestro equipo combina 
              tecnología avanzada con sensibilidad artística para dar vida a tus proyectos.
            </p>
            <div className="stats-grid">
              <div className="stat-item">
                <span className="stat-number">500+</span>
                <span className="stat-label">Proyectos Realizados</span>
              </div>
              <div className="stat-item">
                <span className="stat-number">15+</span>
                <span className="stat-label">Años de Experiencia</span>
              </div>
              <div className="stat-item">
                <span className="stat-number">98%</span>
                <span className="stat-label">Clientes Satisfechos</span>
              </div>
            </div>
          </div>
        </section>

        {/* Services Section */}
        <section className="section services-section">
          <div className="section-container">
            <h2 className="section-title" data-scroll="fadeInUp">Nuestros Servicios</h2>
            <div className="services-grid">
              <div className="service-card" data-scroll="fadeInLeft">
                <div className="service-icon">🏢</div>
                <h3 className="service-title">Renders Arquitectónicos</h3>
                <p className="service-description">
                  Visualizaciones precisas de edificios y estructuras con iluminación realista.
                </p>
              </div>
              <div className="service-card" data-scroll="fadeInUp">
                <div className="service-icon">🏠</div>
                <h3 className="service-title">Interiores</h3>
                <p className="service-description">
                  Representaciones detalladas de espacios interiores con materiales y texturas reales.
                </p>
              </div>
              <div className="service-card" data-scroll="fadeInRight">
                <div className="service-icon">🌆</div>
                <h3 className="service-title">Masterplans</h3>
                <p className="service-description">
                  Visualización de proyectos urbanísticos a gran escala.
                </p>
              </div>
            </div>
          </div>
        </section>

        {/* Portfolio Preview */}
        <section className="section portfolio-section">
          <div className="section-container">
            <h2 className="section-title" data-scroll="fadeInUp">Portafolio Destacado</h2>
            <p className="section-text" data-scroll="fadeInUp">
              Algunos de nuestros trabajos más recientes que muestran la calidad y detalle de nuestros renders.
            </p>
            <div className="portfolio-grid">
              {images.slice(0, 4).map((img, index) => (
                <div 
                  key={`portfolio-${index}`} 
                  className="portfolio-item"
                  data-scroll={index % 2 === 0 ? "fadeInLeft" : "fadeInRight"}
                >
                  <img src={img} alt={`Proyecto ${index + 1}`} className="portfolio-image" />
                  <div className="portfolio-overlay">
                    <h3 className="portfolio-title">Proyecto {index + 1}</h3>
                    <button className="portfolio-button">Ver Detalles</button>
                  </div>
                </div>
              ))}
            </div>
            <div className="section-cta" data-scroll="fadeInUp">
              <a href="/portafolio" className="cta-button">Ver Portafolio Completo</a>
            </div>
          </div>
        </section>

        {/* CTA Section */}
        <section className="section cta-section">
          <div className="section-container">
            <h2 className="section-title" data-scroll="fadeInUp">¿Listo para transformar tu proyecto?</h2>
            <p className="section-text" data-scroll="fadeInUp">
              Contáctanos hoy mismo para discutir cómo podemos ayudarte a visualizar tu visión arquitectónica.
            </p>
            <div className="cta-buttons" data-scroll="fadeInUp">
              <a href="/contacto" className="cta-button primary">Solicitar Presupuesto</a>
              <a href="/cursos" className="cta-button secondary">Aprende con Nosotros</a>
            </div>
          </div>
        </section>
      </main>
    </div>
  );
}

export default Home;
