import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './LoginPage.css';

function LoginPage() {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false); // Estado para controlar la visibilidad de la contrase√±a
  const navigate = useNavigate();
  const { login } = useAuth();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword); // Alternar la visibilidad de la contrase√±a
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData),
        credentials: 'include'
      });

      const contentType = response.headers.get('content-type');
      let data;

      if (contentType && contentType.includes('application/json')) {
        data = await response.json();
      } else {
        const text = await response.text();
        throw new Error(`Error en la respuesta del servidor: ${text}`);
      }

      if (!response.ok) {
        throw new Error(data.error || data.message || 'Error al iniciar sesi√≥n');
      }

      login(data.token);
      navigate('/', { replace: true });
    } catch (err) {
      setError(err.message || 'Error de red al intentar conectar con el servidor');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-page animate__animated animate__fadeIn">
      <h2>Iniciar Sesi√≥n</h2>

      <form className="login-form" onSubmit={handleSubmit}>
        {error && <div className="error-message">{error}</div>}

        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
            className="form-input"
            placeholder=" "
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Contrase√±a</label>
          <div className="password-input-container">
            <input
              type={showPassword ? 'text' : 'password'} // Cambiar el tipo de input seg√∫n el estado
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              className="form-input"
              placeholder=" "
            />
            <button
              type="button"
              className="password-toggle-btn"
              onClick={togglePasswordVisibility}
            >
              {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
            </button>
          </div>
        </div>

        <div className="forgot-password">
          <a href="/recuperar-contrasena">¬øOlvidaste tu contrase√±a?</a>
        </div>

        <button type="submit" className="submit-btn" disabled={loading}>
          {loading ? 'Procesando...' : 'Iniciar Sesi√≥n'}
        </button>
      </form>

      <div className="register-link">
        ¬øNo tienes cuenta? <a href="/registro">Reg√≠strate aqu√≠</a>
      </div>
    </div>
  );
}

export default LoginPage;
