import React, { useState, useEffect, useRef } from 'react';
import './Home.css';

function Home() {
  const [scrolled, setScrolled] = useState(false);
  const [images, setImages] = useState([]);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const heroSectionRef = useRef(null);
  const contentElementsRef = useRef([]);

  // Cargar im√°genes y configurar intervalos
  useEffect(() => {
    // Cargar im√°genes (simulando carga as√≠ncrona)
    const loadImages = async () => {
      try {
        // En un caso real, podr√≠as cargarlas desde una API o archivos locales
        const loadedImages = [
          process.env.PUBLIC_URL + '/img/render7.jpge',
          process.env.PUBLIC_URL + '/img/render8.jpg'
        ];
        setImages(loadedImages);

        // Iniciar el cambio autom√°tico de im√°genes
        const interval = setInterval(() => {
          setCurrentImageIndex(prev => (prev + 1) % loadedImages.length);
        }, 3000);

        return () => clearInterval(interval);
      } catch (error) {
        console.error("Error loading images:", error);
      }
    };

    loadImages();
  }, []);

  // Efecto de scroll
  useEffect(() => {
    const handleScroll = () => {
      // Efecto en el hero section
      if (heroSectionRef.current) {
        const heroHeight = heroSectionRef.current.offsetHeight;
        const scrollPosition = window.scrollY;
        setScrolled(scrollPosition > heroHeight * 0.2);
      }

      // Animaciones para elementos del contenido
      contentElementsRef.current.forEach(el => {
        if (el) {
          const rect = el.getBoundingClientRect();
          const isVisible = rect.top < window.innerHeight * 0.8;
          if (isVisible) {
            el.classList.add('animated');
          }
        }
      });
    };

    // Inicializar elementos a animar
    contentElementsRef.current = Array.from(document.querySelectorAll('[data-scroll]'));

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Ejecutar al cargar para elementos visibles inicialmente

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="home">
      {/* Hero Section con im√°genes horizontales */}
      <section 
        ref={heroSectionRef}
        className={`hero-section ${scrolled ? 'scroll-effect' : ''}`}
      >
        <div className="horizontal-images-container">
          {images.map((img, index) => (
            <img 
              key={index}
              src={img}
              alt={`Imagen ${index + 1}`}
              className={`horizontal-image ${index === currentImageIndex ? 'active' : ''}`}
              loading="eager"
              style={{
                opacity: index === currentImageIndex ? 1 : 0,
                transform: `translateX(${index * 100}%)`
              }}
            />
          ))}
        </div>
        <div className="image-overlay"></div>
      </section>

      {/* Contenido principal */}
      <section className="content-section">
        <div className="hero-content">
          <div className="hero-container">
            <h1 
              className="hero-title"
              data-scroll="fadeInUp"
            >
              Plataforma de Cursos Premium
            </h1>

            <p 
              className="hero-subtitle"
              data-scroll="fadeInUp"
            >
              Domina las habilidades m√°s demandadas con nuestros cursos especializados.
            </p>

            <div 
              className="hero-cta-container"
              data-scroll="fadeInUp"
            >
              <a href="/cursos" className="hero-cta">Explorar Cursos</a>
              <a href="/registro" className="hero-cta secondary">Prueba Gratis</a>
            </div>

            <div className="features-grid">
              <div 
                className="feature-card"
                data-scroll="fadeInLeft"
              >
                <div className="feature-icon">üéì</div>
                <h3 className="feature-title">Certificaci√≥n</h3>
                <p className="feature-description">Obt√©n certificados reconocidos en la industria</p>
              </div>

              <div 
                className="feature-card"
                data-scroll="fadeInUp"
              >
                <div className="feature-icon">üë®‚Äçüíª</div>
                <h3 className="feature-title">Proyectos Reales</h3>
                <p className="feature-description">Aprende trabajando en casos pr√°cticos actuales</p>
              </div>

              <div 
                className="feature-card"
                data-scroll="fadeInRight"
              >
                <div className="feature-icon">üì±</div>
                <h3 className="feature-title">Acceso M√≥vil</h3>
                <p className="feature-description">Aprende desde cualquier dispositivo</p>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
}

export default Home;
