import React, { useState, useRef, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './CursoDetalle.css';

function CursoDetalle() {
  const { id } = useParams();
  const videoRef = useRef(null);
  const progressRef = useRef(null);
  const [currentVideo, setCurrentVideo] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [progress, setProgress] = useState(0);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [videoProgress, setVideoProgress] = useState({});
  const [overallProgress, setOverallProgress] = useState(0);

  // Datos de prueba para los cursos
  const cursos = [
    {
      id: 1,
      titulo: 'Curso de React',
      descripcion: 'Aprende React desde cero hasta un nivel avanzado con proyectos prácticos.',
      contenido: 'En este curso aprenderás todos los conceptos fundamentales de React, componentes, estados, props, hooks, y mucho más.',
      videos: [
        {
          id: 1,
          titulo: 'Introducción a React',
          url: 'https://www.learningcontainer.com/wp-content/uploads/2020/05/sample-mp4-file.mp4',
          duracion: '8:24'
        },
        {
          id: 2,
          titulo: 'Componentes y Props',
          url: 'https://www.learningcontainer.com/wp-content/uploads/2020/05/sample-mp4-file.mp4',
          duracion: '10:15'
        },
        {
          id: 3,
          titulo: 'Estado y Ciclo de Vida',
          url: 'https://www.learningcontainer.com/wp-content/uploads/2020/05/sample-mp4-file.mp4',
          duracion: '12:50'
        },
        {
          id: 4,
          titulo: 'Hooks en React',
          url: 'https://www.learningcontainer.com/wp-content/uploads/2020/05/sample-mp4-file.mp4',
          duracion: '15:30'
        }
      ]
    },
    {
      id: 2,
      titulo: 'Curso de Node.js',
      descripcion: 'Domina el backend con Node.js y construye APIs robustas.',
      contenido: 'Aprenderás a utilizar Node.js para crear aplicaciones de servidor, APIs RESTful y mucho más.',
      videos: [
        {
          id: 1,
          titulo: 'Introducción a Node.js',
          url: 'https://www.learningcontainer.com/wp-content/uploads/2020/05/sample-mp4-file.mp4',
          duracion: '7:15'
        },
        {
          id: 2,
          titulo: 'Módulos y NPM',
          url: 'https://www.learningcontainer.com/wp-content/uploads/2020/05/sample-mp4-file.mp4',
          duracion: '9:30'
        },
        {
          id: 3,
          titulo: 'Express.js',
          url: 'https://www.learningcontainer.com/wp-content/uploads/2020/05/sample-mp4-file.mp4',
          duracion: '11:45'
        }
      ]
    },
    {
      id: 3,
      titulo: 'Curso de Diseño Web',
      descripcion: 'Crea diseños modernos y responsivos utilizando HTML5 y CSS3.',
      contenido: 'Aprenderás los fundamentos del diseño web moderno, incluyendo flex, grid y animaciones CSS.',
      videos: [
        {
          id: 1,
          titulo: 'Fundamentos de HTML5',
          url: 'https://www.learningcontainer.com/wp-content/uploads/2020/05/sample-mp4-file.mp4',
          duracion: '6:45'
        },
        {
          id: 2,
          titulo: 'CSS Moderno',
          url: 'https://www.learningcontainer.com/wp-content/uploads/2020/05/sample-mp4-file.mp4',
          duracion: '10:20'
        },
        {
          id: 3,
          titulo: 'Diseño Responsivo',
          url: 'https://www.learningcontainer.com/wp-content/uploads/2020/05/sample-mp4-file.mp4',
          duracion: '8:55'
        },
        {
          id: 4,
          titulo: 'Animaciones CSS',
          url: 'https://www.learningcontainer.com/wp-content/uploads/2020/05/sample-mp4-file.mp4',
          duracion: '7:40'
        },
        {
          id: 5,
          titulo: 'Flexbox y Grid',
          url: 'https://www.learningcontainer.com/wp-content/uploads/2020/05/sample-mp4-file.mp4',
          duracion: '12:10'
        }
      ]
    }
  ];

  const curso = cursos.find((c) => c.id === parseInt(id));

  // Inicializar el progreso del video desde localStorage al cargar
  useEffect(() => {
    if (curso) {
      const savedProgress = localStorage.getItem(`curso_progress_${curso.id}`);
      const savedVideoProgress = localStorage.getItem(`curso_video_progress_${curso.id}`);

      if (savedProgress) {
        setOverallProgress(JSON.parse(savedProgress));
      }

      if (savedVideoProgress) {
        setVideoProgress(JSON.parse(savedVideoProgress));
      } else {
        // Inicializar el progreso de cada video a 0
        const initialProgress = {};
        curso.videos.forEach(video => {
          initialProgress[video.id] = 0;
        });
        setVideoProgress(initialProgress);
      }
    }
  }, [curso]);

  // Actualizar el progreso general del curso
  useEffect(() => {
    if (curso && Object.keys(videoProgress).length > 0) {
      const completedVideos = Object.values(videoProgress).filter(progress => progress >= 90).length;
      const totalVideos = curso.videos.length;
      const newOverallProgress = Math.round((completedVideos / totalVideos) * 100);

      setOverallProgress(newOverallProgress);
      localStorage.setItem(`curso_progress_${curso.id}`, JSON.stringify(newOverallProgress));
      localStorage.setItem(`curso_video_progress_${curso.id}`, JSON.stringify(videoProgress));
    }
  }, [videoProgress, curso]);

  // Manejar eventos del video
  useEffect(() => {
    const videoElement = videoRef.current;

    if (videoElement) {
      const handleTimeUpdate = () => {
        const current = videoElement.currentTime;
        const videoDuration = videoElement.duration;

        if (videoDuration > 0) {
          const progressPercent = (current / videoDuration) * 100;
          setProgress(progressPercent);
          setCurrentTime(current);

          // Actualizar el progreso de este video específico
          if (curso && curso.videos[currentVideo]) {
            const videoId = curso.videos[currentVideo].id;
            setVideoProgress(prev => ({
              ...prev,
              [videoId]: progressPercent
            }));
          }
        }
      };

      const handleDurationChange = () => {
        setDuration(videoElement.duration);
      };

      const handlePlay = () => {
        setIsPlaying(true);
      };

      const handlePause = () => {
        setIsPlaying(false);
      };

      const handleEnded = () => {
        setIsPlaying(false);

        // Marcar el video como completado
        if (curso && curso.videos[currentVideo]) {
          const videoId = curso.videos[currentVideo].id;
          setVideoProgress(prev => ({
            ...prev,
            [videoId]: 100
          }));
        }

        // Avanzar al siguiente video automáticamente si existe
        if (curso && currentVideo < curso.videos.length - 1) {
          setCurrentVideo(currentVideo + 1);
        }
      };

      // Agregar event listeners
      videoElement.addEventListener('timeupdate', handleTimeUpdate);
      videoElement.addEventListener('durationchange', handleDurationChange);
      videoElement.addEventListener('play', handlePlay);
      videoElement.addEventListener('pause', handlePause);
      videoElement.addEventListener('ended', handleEnded);

      // Limpiar event listeners
      return () => {
        videoElement.removeEventListener('timeupdate', handleTimeUpdate);
        videoElement.removeEventListener('durationchange', handleDurationChange);
        videoElement.removeEventListener('play', handlePlay);
        videoElement.removeEventListener('pause', handlePause);
        videoElement.removeEventListener('ended', handleEnded);
      };
    }
  }, [currentVideo, curso]);

  // Cambiar de video
  const changeVideo = (index) => {
    setCurrentVideo(index);
    setProgress(0);
    setCurrentTime(0);
    setIsPlaying(false);
  };

  // Funciones de control de reproducción
  const togglePlay = () => {
    if (videoRef.current.paused) {
      videoRef.current.play();
    } else {
      videoRef.current.pause();
    }
  };

  const handleBackward = () => {
    videoRef.current.currentTime = Math.max(0, videoRef.current.currentTime - 10);
  };

  const handleForward = () => {
    videoRef.current.currentTime = Math.min(
      videoRef.current.duration,
      videoRef.current.currentTime + 10
    );
  };

  const handleProgressClick = (e) => {
    const progressBar = progressRef.current;
    const position = (e.pageX - progressBar.getBoundingClientRect().left) / progressBar.offsetWidth;
    videoRef.current.currentTime = position * videoRef.current.duration;
  };

  // Formatear tiempo
  const formatTime = (timeInSeconds) => {
    const minutes = Math.floor(timeInSeconds / 60);
    const seconds = Math.floor(timeInSeconds % 60);
    return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
  };

  if (!curso) {
    return <div className="error">Curso no encontrado</div>;
  }

  return (
    <div className="curso-detalle animate__animated animate__fadeIn">
      <h2>{curso.titulo}</h2>
      <p>{curso.descripcion}</p>

      {/* Progreso del curso */}
      <div className="course-progress">
        <div className="progress-header">
          <span className="progress-title">Progreso del curso</span>
          <span className="progress-percentage">{overallProgress}%</span>
        </div>
        <div className="progress-bar-container">
          <div 
            className="progress-bar-fill"
            style={{ width: `${overallProgress}%` }}
          ></div>
        </div>
      </div>

      {/* Reproductor de video */}
      <div className="video-section">
        <div className="video-container">
          <video
            ref={videoRef}
            className="video-player"
            src={curso.videos[currentVideo]?.url}
            poster="/path-to-poster-image.jpg"
          ></video>
        </div>

        {/* Controles personalizados */}
        <div className="video-controls">
          <button className="control-button" onClick={handleBackward} title="Retroceder 10 segundos">
            ⏪
          </button>

          <button className="control-button" onClick={togglePlay} title={isPlaying ? "Pausar" : "Reproducir"}>
            {isPlaying ? "⏸️" : "▶️"}
          </button>

          <button className="control-button" onClick={handleForward} title="Avanzar 10 segundos">
            ⏩
          </button>

          <div 
            className="video-progress" 
            ref={progressRef}
            onClick={handleProgressClick}
          >
            <div 
              className="progress-bar"
              style={{ width: `${progress}%` }}
            ></div>
          </div>

          <div className="time-display">
            {formatTime(currentTime)} / {formatTime(duration)}
          </div>
        </div>

        {/* Navegación entre videos */}
        <div className="lesson-navigation">
          <button 
            className="nav-button"
            onClick={() => changeVideo(currentVideo - 1)}
            disabled={currentVideo === 0}
          >
            ◀ Video anterior
          </button>

          <button 
            className="nav-button"
            onClick={() => changeVideo(currentVideo + 1)}
            disabled={currentVideo === curso.videos.length - 1}
          >
            Video siguiente ▶
          </button>
        </div>
      </div>

      {/* Lista de videos/lecciones */}
      <div className="lessons-list">
        <h3>Contenido del curso</h3>
        {curso.videos.map((video, index) => (
          <div 
            key={video.id}
            className={`lesson-item ${index === currentVideo ? 'active' : ''} ${videoProgress[video.id] >= 90 ? 'completed' : ''}`}
            onClick={() => changeVideo(index)}
          >
            <span className="lesson-status">
              {videoProgress[video.id] >= 90 ? '✅' : index === currentVideo ? '▶️' : '📚'}
            </span>
            <span className="lesson-title">{video.titulo}</span>
            <span className="lesson-duration">{video.duracion}</span>
          </div>
        ))}
      </div>

      <div className="contenido">
        <h3>Descripción del curso</h3>
        <p>{curso.contenido}</p>
      </div>
    </div>
  );
}

export default CursoDetalle;
