import React, { useState, useRef, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './CursoDetalle.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faBackward, faForward, faPlay, faPause,
  faCheckCircle, faBook, faPlayCircle,
  faChevronLeft, faChevronRight, faArrowLeft, faHome
} from '@fortawesome/free-solid-svg-icons';
import { faCircle as faCircleRegular } from '@fortawesome/free-regular-svg-icons';
import 'animate.css';

function CursoDetalle() {
  const { id } = useParams();
  const navigate = useNavigate();
  const videoRef = useRef(null);
  const progressRef = useRef(null);
  const [currentVideo, setCurrentVideo] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [progress, setProgress] = useState(0);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [videoProgress, setVideoProgress] = useState({});
  const [overallProgress, setOverallProgress] = useState(0);
  const [completedVideos, setCompletedVideos] = useState({});
  const [loading, setLoading] = useState(true);

  // Datos de prueba para los cursos
  const cursos = [
    {
      id: 1,
      titulo: 'Curso de React',
      descripcion: 'Aprende React desde cero hasta un nivel avanzado con proyectos prácticos.',
      contenido: 'En este curso aprenderás todos los conceptos fundamentales de React, componentes, estados, props, hooks, y mucho más.',
      videos: [
        {
          id: 1,
          titulo: 'Introducción a React',
          url: 'https://www.learningcontainer.com/wp-content/uploads/2020/05/sample-mp4-file.mp4',
          duracion: '8:24'
        },
        {
          id: 2,
          titulo: 'Componentes y Props',
          url: 'https://filesamples.com/samples/video/mp4/sample_640x360.mp4',
          duracion: '10:15'
        },
        {
          id: 3,
          titulo: 'Estado y Ciclo de Vida',
          url: 'https://filesamples.com/samples/video/mp4/sample_960x540.mp4',
          duracion: '12:50'
        },
        {
          id: 4,
          titulo: 'Hooks en React',
          url: 'https://filesamples.com/samples/video/mp4/sample_1280x720.mp4',
          duracion: '15:30'
        }
      ]
    },
    {
      id: 2,
      titulo: 'Curso de Node.js',
      descripcion: 'Domina el backend con Node.js y construye APIs robustas.',
      contenido: 'Aprenderás a utilizar Node.js para crear aplicaciones de servidor, APIs RESTful y mucho más.',
      videos: [
        {
          id: 1,
          titulo: 'Introducción a Node.js',
          url: 'https://filesamples.com/samples/video/mp4/sample_1920x1080.mp4',
          duracion: '7:15'
        },
        {
          id: 2,
          titulo: 'Módulos y NPM',
          url: 'https://www.learningcontainer.com/wp-content/uploads/2020/05/sample-mp4-file.mp4',
          duracion: '9:30'
        },
        {
          id: 3,
          titulo: 'Express.js',
          url: 'https://filesamples.com/samples/video/mp4/sample_640x360.mp4',
          duracion: '11:45'
        }
      ]
    },
    {
      id: 3,
      titulo: 'Curso de Diseño Web',
      descripcion: 'Crea diseños modernos y responsivos utilizando HTML5 y CSS3.',
      contenido: 'Aprenderás los fundamentos del diseño web moderno, incluyendo flex, grid y animaciones CSS.',
      videos: [
        {
          id: 1,
          titulo: 'Fundamentos de HTML5',
          url: 'https://filesamples.com/samples/video/mp4/sample_960x540.mp4',
          duracion: '6:45'
        },
        {
          id: 2,
          titulo: 'CSS Moderno',
          url: 'https://filesamples.com/samples/video/mp4/sample_1280x720.mp4',
          duracion: '10:20'
        },
        {
          id: 3,
          titulo: 'Diseño Responsivo',
          url: 'https://www.learningcontainer.com/wp-content/uploads/2020/05/sample-mp4-file.mp4',
          duracion: '8:55'
        },
        {
          id: 4,
          titulo: 'Animaciones CSS',
          url: 'https://filesamples.com/samples/video/mp4/sample_1920x1080.mp4',
          duracion: '7:40'
        },
        {
          id: 5,
          titulo: 'Flexbox y Grid',
          url: 'https://filesamples.com/samples/video/mp4/sample_640x360.mp4',
          duracion: '12:10'
        }
      ]
    }
  ];

  // Buscar el curso actual por ID
  const curso = cursos.find((c) => c.id === parseInt(id));

  // Limpieza al desmontar el componente
  useEffect(() => {
    const videoElement = videoRef.current;

    return () => {
      if (videoElement) {
        videoElement.pause();
        videoElement.src = "";
        videoElement.load();
      }
    };
  }, []);

  // Inicializar el progreso del video y los videos completados desde localStorage al cargar
  useEffect(() => {
    if (curso) {
      setLoading(true);

      try {
        const savedProgress = localStorage.getItem(`curso_progress_${curso.id}`);
        const savedVideoProgress = localStorage.getItem(`curso_video_progress_${curso.id}`);
        const savedCompletedVideos = localStorage.getItem(`curso_completed_videos_${curso.id}`);

        if (savedProgress) {
          setOverallProgress(JSON.parse(savedProgress));
        }

        if (savedVideoProgress) {
          setVideoProgress(JSON.parse(savedVideoProgress));
        } else {
          const initialProgress = {};
          curso.videos.forEach(video => {
            initialProgress[video.id] = 0;
          });
          setVideoProgress(initialProgress);
        }

        if (savedCompletedVideos) {
          setCompletedVideos(JSON.parse(savedCompletedVideos));
        } else {
          const initialCompleted = {};
          curso.videos.forEach(video => {
            initialCompleted[video.id] = false;
          });
          setCompletedVideos(initialCompleted);
        }
      } catch (error) {
        console.error("Error al cargar datos desde localStorage:", error);

        const initialProgress = {};
        const initialCompleted = {};
        curso.videos.forEach(video => {
          initialProgress[video.id] = 0;
          initialCompleted[video.id] = false;
        });
        setVideoProgress(initialProgress);
        setCompletedVideos(initialCompleted);
      }

      setLoading(false);
    }
  }, [curso]);

  // Actualizar el progreso general del curso
  useEffect(() => {
    if (curso && Object.keys(completedVideos).length > 0) {
      const completedCount = Object.values(completedVideos).filter(completed => completed).length;
      const totalVideos = curso.videos.length;
      const newOverallProgress = Math.round((completedCount / totalVideos) * 100);

      setOverallProgress(newOverallProgress);

      try {
        localStorage.setItem(`curso_progress_${curso.id}`, JSON.stringify(newOverallProgress));
      } catch (error) {
        console.error("Error al guardar progreso en localStorage:", error);
      }
    }
  }, [completedVideos, curso]);

  // Guardar el progreso de los videos
  useEffect(() => {
    if (curso && Object.keys(videoProgress).length > 0) {
      try {
        localStorage.setItem(`curso_video_progress_${curso.id}`, JSON.stringify(videoProgress));
      } catch (error) {
        console.error("Error al guardar progreso de videos en localStorage:", error);
      }
    }
  }, [videoProgress, curso]);

  // Manejar eventos del video
  useEffect(() => {
    const videoElement = videoRef.current;

    if (videoElement) {
      const handleTimeUpdate = () => {
        const current = videoElement.currentTime;
        const videoDuration = videoElement.duration || 0;

        if (videoDuration > 0) {
          const progressPercent = (current / videoDuration) * 100;
          setProgress(progressPercent);
          setCurrentTime(current);

          if (curso && curso.videos[currentVideo]) {
            const videoId = curso.videos[currentVideo].id;
            setVideoProgress(prev => ({
              ...prev,
              [videoId]: progressPercent
            }));
          }
        }
      };

      const handleDurationChange = () => {
        setDuration(videoElement.duration || 0);
      };

      const handlePlay = () => {
        setIsPlaying(true);
      };

      const handlePause = () => {
        setIsPlaying(false);
      };

      const handleEnded = () => {
        setIsPlaying(false);

        if (curso && curso.videos[currentVideo]) {
          const videoId = curso.videos[currentVideo].id;
          setCompletedVideos(prev => {
            const newState = {
              ...prev,
              [videoId]: true
            };
            try {
              localStorage.setItem(`curso_completed_videos_${curso.id}`, JSON.stringify(newState));
            } catch (error) {
              console.error("Error al guardar videos completados:", error);
            }
            return newState;
          });
        }

        if (curso && currentVideo < curso.videos.length - 1) {
          setCurrentVideo(currentVideo + 1);
        }
      };

      const handleError = (error) => {
        console.error("Error en la reproducción del video:", error);
        setIsPlaying(false);
      };

      const handleCanPlay = () => {
        if (videoProgress[curso.videos[currentVideo]?.id] > 0 && videoProgress[curso.videos[currentVideo]?.id] < 95) {
          const savedProgressSeconds = (videoProgress[curso.videos[currentVideo]?.id] / 100) * videoElement.duration;
          if (savedProgressSeconds > 0 && !isNaN(savedProgressSeconds)) {
            videoElement.currentTime = savedProgressSeconds;
          }
        }
      };

      videoElement.addEventListener('timeupdate', handleTimeUpdate);
      videoElement.addEventListener('durationchange', handleDurationChange);
      videoElement.addEventListener('play', handlePlay);
      videoElement.addEventListener('pause', handlePause);
      videoElement.addEventListener('ended', handleEnded);
      videoElement.addEventListener('error', handleError);
      videoElement.addEventListener('canplay', handleCanPlay);

      return () => {
        videoElement.removeEventListener('timeupdate', handleTimeUpdate);
        videoElement.removeEventListener('durationchange', handleDurationChange);
        videoElement.removeEventListener('play', handlePlay);
        videoElement.removeEventListener('pause', handlePause);
        videoElement.removeEventListener('ended', handleEnded);
        videoElement.removeEventListener('error', handleError);
        videoElement.removeEventListener('canplay', handleCanPlay);
      };
    }
  }, [currentVideo, curso, videoProgress]);

  // Cambiar de video
  const changeVideo = (index) => {
    if (index >= 0 && index < curso.videos.length) {
      setCurrentVideo(index);
    }
  };

  // Funciones de control de reproducción
  const togglePlay = () => {
    if (videoRef.current) {
      if (videoRef.current.paused) {
        const playPromise = videoRef.current.play();

        if (playPromise !== undefined) {
          playPromise.catch(error => {
            console.error("Error al reproducir:", error);
          });
        }
      } else {
        videoRef.current.pause();
      }
    }
  };

  const handleBackward = () => {
    if (videoRef.current) {
      videoRef.current.currentTime = Math.max(0, videoRef.current.currentTime - 10);
    }
  };

  const handleForward = () => {
    if (videoRef.current) {
      videoRef.current.currentTime = Math.min(
        videoRef.current.duration || 0,
        videoRef.current.currentTime + 10
      );
    }
  };

  const handleProgressClick = (e) => {
    if (progressRef.current && videoRef.current) {
      const progressBar = progressRef.current;
      const rect = progressBar.getBoundingClientRect();
      const position = (e.clientX - rect.left) / progressBar.offsetWidth;
      const newTime = position * (videoRef.current.duration || 0);

      if (newTime >= 0 && newTime <= (videoRef.current.duration || 0)) {
        videoRef.current.currentTime = newTime;
      }
    }
  };

  // Formatear tiempo
  const formatTime = (timeInSeconds) => {
    if (isNaN(timeInSeconds) || timeInSeconds < 0) return "0:00";

    const minutes = Math.floor(timeInSeconds / 60);
    const seconds = Math.floor(timeInSeconds % 60);
    return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
  };

  // Marcar video como completado manualmente
  const markAsCompleted = (videoId) => {
    setCompletedVideos(prev => {
      const newState = {
        ...prev,
        [videoId]: !prev[videoId]
      };

      try {
        localStorage.setItem(`curso_completed_videos_${curso.id}`, JSON.stringify(newState));
      } catch (error) {
        console.error("Error al guardar el estado de completado:", error);
      }

      return newState;
    });
  };

  // Función para navegar a la página de cursos - CORREGIDA
  const volverACursos = (e) => {
    e.preventDefault(); // Prevenir comportamiento por defecto
    // Pausar video si está reproduciéndose
    if (videoRef.current && !videoRef.current.paused) {
      videoRef.current.pause();
    }
    // Navegación con retraso de 100ms para asegurar que se detengan todos los procesos
    setTimeout(() => {
      navigate('/cursos');
    }, 100);
  };

  // Función para navegar a la página principal - CORREGIDA
  const irAInicio = (e) => {
    e.preventDefault(); // Prevenir comportamiento por defecto
    // Pausar video si está reproduciéndose
    if (videoRef.current && !videoRef.current.paused) {
      videoRef.current.pause();
    }
    // Navegación con retraso de 100ms para asegurar que se detengan todos los procesos
    setTimeout(() => {
      navigate('/');
    }, 100);
  };

  // Función para navegar al siguiente curso - CORREGIDA
  const irASiguienteCurso = (e) => {
    e.preventDefault(); // Prevenir comportamiento por defecto
    if (curso) {
      const cursoActualIndex = cursos.findIndex(c => c.id === curso.id);
      if (cursoActualIndex < cursos.length - 1) {
        // Pausar video si está reproduciéndose
        if (videoRef.current && !videoRef.current.paused) {
          videoRef.current.pause();
        }
        // Navegación con retraso de 100ms para asegurar que se detengan todos los procesos
        setTimeout(() => {
          navigate(`/curso/${cursos[cursoActualIndex + 1].id}`);
        }, 100);
      }
    }
  };

  // Función para navegar al curso anterior - CORREGIDA
  const irACursoAnterior = (e) => {
    e.preventDefault(); // Prevenir comportamiento por defecto
    if (curso) {
      const cursoActualIndex = cursos.findIndex(c => c.id === curso.id);
      if (cursoActualIndex > 0) {
        // Pausar video si está reproduciéndose
        if (videoRef.current && !videoRef.current.paused) {
          videoRef.current.pause();
        }
        // Navegación con retraso de 100ms para asegurar que se detengan todos los procesos
        setTimeout(() => {
          navigate(`/curso/${cursos[cursoActualIndex - 1].id}`);
        }, 100);
      }
    }
  };

  if (!curso) {
    return <div className="error">Curso no encontrado</div>;
  }

  if (loading) {
    return <div className="loading">Cargando curso...</div>;
  }

  return (
    <div className="curso-detalle animate__animated animate__fadeIn">
      <div className="navigation-buttons">
        <button className="nav-button" onClick={volverACursos}>
          <FontAwesomeIcon icon={faArrowLeft} /> Volver a Cursos
        </button>
        <button className="nav-button" onClick={irAInicio}>
          <FontAwesomeIcon icon={faHome} /> Inicio
        </button>
      </div>

      <h2>{curso.titulo}</h2>
      <p>{curso.descripcion}</p>

      {/* Progreso del curso */}
      <div className="course-progress">
        <div className="progress-header">
          <span className="progress-title">Progreso del curso</span>
          <span className="progress-percentage">{overallProgress}%</span>
        </div>
        <div className="progress-bar-container">
          <div
            className="progress-bar-fill"
            style={{ width: `${overallProgress}%` }}
          ></div>
        </div>
      </div>

      {/* Reproductor de video */}
      <div className="video-section">
        <div className="video-container">
          <video
            ref={videoRef}
            className="video-player"
            src={curso.videos[currentVideo]?.url}
            poster="/path-to-poster-image.jpg"
            preload="metadata"
            controlsList="nodownload"
          ></video>
        </div>

        {/* Controles personalizados */}
        <div className="video-controls">
          <button className="control-button" onClick={handleBackward} title="Retroceder 10 segundos">
            <FontAwesomeIcon icon={faBackward} />
          </button>

          <button className="control-button" onClick={togglePlay} title={isPlaying ? "Pausar" : "Reproducir"}>
            <FontAwesomeIcon icon={isPlaying ? faPause : faPlay} />
          </button>

          <button className="control-button" onClick={handleForward} title="Avanzar 10 segundos">
            <FontAwesomeIcon icon={faForward} />
          </button>

          <div
            className="video-progress"
            ref={progressRef}
            onClick={handleProgressClick}
          >
            <div
              className="progress-bar"
              style={{ width: `${progress}%` }}
            ></div>
          </div>

          <div className="time-display">
            {formatTime(currentTime)} / {formatTime(duration)}
          </div>
        </div>

        {/* Navegación entre videos y botón de completado */}
        <div className="video-navigation">
          <div className="lesson-navigation">
            <button
              className="nav-button"
              onClick={() => changeVideo(currentVideo - 1)}
              disabled={currentVideo === 0}
            >
              <FontAwesomeIcon icon={faChevronLeft} /> Video anterior
            </button>

            <button
              className={`complete-button ${completedVideos[curso.videos[currentVideo]?.id] ? 'completed' : ''}`}
              onClick={() => markAsCompleted(curso.videos[currentVideo]?.id)}
              title={completedVideos[curso.videos[currentVideo]?.id] ? "Marcar como no completado" : "Marcar como completado"}
            >
              {completedVideos[curso.videos[currentVideo]?.id] ?
                <><FontAwesomeIcon icon={faCheckCircle} /> Completado</> :
                <><FontAwesomeIcon icon={faCircleRegular} /> Marcar como completado</>
              }
            </button>

            <button
              className="nav-button"
              onClick={() => changeVideo(currentVideo + 1)}
              disabled={currentVideo === curso.videos.length - 1}
            >
              Video siguiente <FontAwesomeIcon icon={faChevronRight} />
            </button>
          </div>
        </div>
      </div>

      {/* Lista de videos/lecciones */}
      <div className="lessons-list">
        <h3>Contenido del curso</h3>
        {curso.videos.map((video, index) => (
          <div
            key={video.id}
            className={`lesson-item ${index === currentVideo ? 'active' : ''} ${completedVideos[video.id] ? 'completed' : ''}`}
            onClick={() => changeVideo(index)}
          >
            <span className="lesson-status">
              {completedVideos[video.id] ?
                <FontAwesomeIcon icon={faCheckCircle} /> :
                index === currentVideo ?
                  <FontAwesomeIcon icon={faPlayCircle} /> :
                  <FontAwesomeIcon icon={faBook} />
              }
            </span>
            <span className="lesson-title">{video.titulo}</span>
            <span className="lesson-duration">{video.duracion}</span>
          </div>
        ))}
      </div>

      <div className="contenido">
        <h3>Descripción del curso</h3>
        <p>{curso.contenido}</p>
      </div>

      {/* Navegación entre cursos */}
      <div className="course-navigation">
        <button
          className="nav-button"
          onClick={irACursoAnterior}
          disabled={cursos.findIndex(c => c.id === curso.id) === 0}
        >
          <FontAwesomeIcon icon={faChevronLeft} /> Curso anterior
        </button>
        <button
          className="nav-button"
          onClick={irASiguienteCurso}
          disabled={cursos.findIndex(c => c.id === curso.id) === cursos.length - 1}
        >
          Curso siguiente <FontAwesomeIcon icon={faChevronRight} />
        </button>
      </div>
    </div>
  );
}

export default CursoDetalle;
