import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './LoginPage.css';

function LoginPage() {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();
  const { login } = useAuth();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData),
        credentials: 'include'
      });

      const contentType = response.headers.get('content-type');
      let data;

      if (contentType && contentType.includes('application/json')) {
        data = await response.json();
      } else {
        const text = await response.text();
        throw new Error(`Error en la respuesta del servidor: ${text}`);
      }

      if (!response.ok) {
        throw new Error(data.error || data.message || 'Error al iniciar sesión');
      }

      // Use the new login method from AuthContext
      login(data.token);
      navigate('/', { replace: true });
    } catch (err) {
      setError(err.message || 'Error de red al intentar conectar con el servidor');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-page animate__animated animate__fadeIn">
      {/* Rest of the component remains the same */}
    </div>
  );
}

export default LoginPage;
