import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

function VideoPlayer() {
  const { id, videoId } = useParams();
  const { user } = useAuth();
  const navigate = useNavigate();
  const [videoData, setVideoData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchVideoData = async () => {
      try {
        // Replace with your actual API endpoint
        const response = await fetch(`http://localhost:5000/api/cursos/${id}/videos/${videoId}`, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });

        if (!response.ok) {
          throw new Error('No se pudo cargar el video');
        }

        const data = await response.json();
        setVideoData(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchVideoData();
  }, [id, videoId]);

  if (loading) return <div>Cargando video...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="video-player-container">
      <h2>{videoData.titulo}</h2>
      {videoData.videoUrl ? (
        <video 
          controls 
          width="100%" 
          src={videoData.videoUrl} 
          className="curso-video"
        >
          Tu navegador no soporta videos HTML5
        </video>
      ) : (
        <p>No hay video disponible</p>
      )}
      <div className="video-description">
        <p>{videoData.descripcion}</p>
      </div>
      <button 
        onClick={() => navigate(`/curso/${id}`)} 
        className="btn btn-secondary"
      >
        Volver al curso
      </button>
    </div>
  );
}

export default VideoPlayer;
